      <span class="token comment">// 从右往左挪动 j 直到找到比 base 小的值停下来</span>
      <span class="token keyword">while</span> <span class="token punctuation">(</span>i <span class="token operator">&lt;</span> j <span class="token operator">&amp;&amp;</span> arr<span class="token punctuation">[</span>j<span class="token punctuation">]</span> <span class="token operator">>=</span> base<span class="token punctuation">)</span> <span class="token punctuation">{</span>
